import { Container, Button, Row, Col } from "react-bootstrap";
import Input from "./Input.js";
import { FloatingLabel, Form } from "react-bootstrap";
import Topbar from "./Topbar";

import React, { useState } from "react";
export default function CenterSubject() {
  const [state, setState] = useState({
    book_name: "",
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",
    surah: {
      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",
    },
  });
  return (
    <>
      <Topbar />
      <Container className="text-center">
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="বইয়ের নাম">
              <Form.Control
                as="textarea"
                placeholder="বইয়ের নাম"
                style={{ height: "5px" }}
                value={state}
                onChange={(e) => {
                  setState({ ...state, book_name: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label={placehold}>
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম"
                style={{ height: "5px" }}
                value={state}
                onChange={(e) => {
                  setState({ ...state, surah: { name: e.target.value } });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label={placehold}>
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম্বার"
                style={{ height: "5px" }}
                value={state}
                onChange={(e) => {
                  setState({ ...state, surah: { number: e.target.value } });
                }}
              />
            </FloatingLabel>
          </Col>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label={placehold}>
              <Form.Control
                as="textarea"
                placeholder="আয়াত নাম্বার"
                style={{ height: "5px" }}
                value={state}
                onChange={(e) => {
                  setState({
                    ...state,
                    surah: { versesNumber: e.target.value },
                  });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <FloatingLabel controlId="floatingTextarea2" label={placehold}>
          <Form.Control
            as="textarea"
            placeholder="আয়াত বাংলা ,আরকি"
            style={{ height: "165px" }}
            value={state}
            onChange={(e) => {
              setState({
                ...state,
                surah: { verses: e.target.value },
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label={placehold}>
          <Form.Control
            as="textarea"
            placeholder="সারসংক্ষেপ"
            style={{ height: "295px" }}
            value={state}
            onChange={(e) => {
              setState({
                ...state,
                surah: { summary: e.target.value },
              });
            }}
          />
        </FloatingLabel>
        <Input
          stat={state}
          value={state.summary}
          set={setState}
          placehold="সারসংক্ষেপ"
          h="295px"
        />
        <Input
          stat={state}
          value={state.ancillary_issues}
          set={setState}
          placehold="আনুসঞ্জিক বিষয়"
          h="295px"
        />
        <div className=" py-25 m-25 bg-warning"> BIG DATA</div>
        <Input
          stat={state}
          value={state.Author_Introduction}
          set={setState}
          placehold="লেখকের পরিচিতি"
          h="295px"
        />
        <Input
          stat={state}
          value={state.Meaning_of_Revelation}
          set={setState}
          placehold="ওহির তাৎর্পয়"
          h="295px"
        />
        <Input
          stat={state}
          value={state.History_of_Quran_Revelation}
          set={setState}
          placehold="কোরআন নাজিলের ইতিহাস"
          h="295px"
        />
        <Input
          stat={state}
          value={state.History_of_Preservation_of_Quran}
          set={setState}
          placehold="কোরআন সংরক্ষনের ইতিহাস"
          h="295px"
        />
        <Input
          stat={state}
          value={state.Misconceptions_about_Tafsir}
          set={setState}
          placehold="তাফসির সম্পর্কে ভুল ধারণা"
          h="295px"
        />
        <div className="d-grid gap-2">
          <Button variant="secondary" size="lg">
            Block level button
          </Button>
        </div>
      </Container>
    </>
  );
}
