import React, { useState } from "react";
export default function Input({ placehold, h }) {
  const [state, setState] = useState({
    book_name: '',
    Author_Introduction: '',
    Meaning_of_Revelation: '',
    History_of_Quran_Revelation: '',
    History_of_Preservation_of_Quran: '',
    Misconceptions_about_Tafsir: '',
    surah: {
      name: '',
      number: '',
      versesNumber: '',
      verses: '',
      summary: '',
      ancillary_issues: ''
    },
  })
  const countHandler = (e) => {
    setState(() => e.target.value);
  }

    return (
      <>
        {state}
        <h3>{placehold}</h3>
        <FloatingLabel controlId="floatingTextarea2" label={placehold}>
          <Form.Control
            as="textarea"
            placeholder="vsefvsdvfs"
            style={{ height: h }}
            value={state}
            onChange={countHandler}

          />
        </FloatingLabel>
      </>
    );
}
