import {
  Container,
  Button,
  Row,
  Col,
  FloatingLabel,
  Form,
} from "react-bootstrap";
import axios from "axios";

import Topbar from "../Topbar";

import React, { useState } from "react";
export default function CenterSubject() {
  const [state, setState] = useState({
    book_name: "",
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",

      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",

  });
  const submit = () => {
    axios
      .post("http://localhost:5000/", {
        book_name: state.book_name,
        Author_Introduction: state.Author_Introduction,
        Meaning_of_Revelation: state.Meaning_of_Revelation,
        History_of_Quran_Revelation: state.History_of_Quran_Revelation,
        History_of_Preservation_of_Quran:
          state.History_of_Preservation_of_Quran,
        Misconceptions_about_Tafsir: state.Misconceptions_about_Tafsir,

        surah: [{
          name: state.name,
          number: state.number,
          versesNumber: state.versesNumber,
          verses: state.verses,
          summary: state.summary,
          ancillary_issues: state.ancillary_issues,
        }]
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  };
  return (
    <>
      {state.name}

      <Container className="text-center m-4">
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="বইয়ের নাম">
              <Form.Control
                as="textarea"
                placeholder="বইয়ের নাম"
                style={{ height: "50px" }}
                value={state.book_name}
                onChange={(e) => {
                  setState({ ...state, book_name: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>

          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="সুরার নাম">
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম"
                style={{ height: "50px" }}
                value={state.name}
                onChange={(e) => {
                  setState({ ...state, name: e.target.value});
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="সুরার নাম্বার">
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম্বার"
                style={{ height: "50px" }}
                value={state.number}
                onChange={(e) => {
                  setState({ ...state,  number: e.target.value});
                }}
              />
            </FloatingLabel>

            <FloatingLabel controlId="floatingTextarea2" label="আয়াত নাম্বার">
              <Form.Control
                as="textarea"
                placeholder="আয়াত নাম্বার"
                style={{ height: "50px" }}
                value={state.versesNumber}
                onChange={(e) => {
                  setState({
                    ...state,
                    versesNumber: e.target.value ,
                  });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <FloatingLabel controlId="floatingTextarea2" label="আয়াত বাংলা,আরবি">
          <Form.Control
            as="textarea"
            placeholder="আয়াত বাংলা ,আরকি"
            style={{ height: "165px" }}
            value={state.verses}
            onChange={(e) => {
              setState({
                ...state,
                verses: e.target.value ,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="সারসংক্ষেপ">
          <Form.Control
            as="textarea"
            placeholder="সারসংক্ষেপ"
            style={{ height: "295px" }}
            value={state.summary}
            onChange={(e) => {
              setState({
                ...state,
              summary: e.target.value ,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="আনুসঞ্জিক বিষয়">
          <Form.Control
            as="textarea"
            placeholder="আনুসঞ্জিক বিষয়"
            style={{ height: "295px" }}
            value={state.ancillary_issues}
            onChange={(e) => {
              setState({
                ...state,
               ancillary_issues: e.target.value ,
              });
            }}
          />
        </FloatingLabel>


        <div className="d-grid gap-2">
          <Button variant="secondary" size="lg" type="submit" onClick={submit} >
            Block level button
          </Button>
        </div>
      </Container>
    </>
  );
}
