import {
  Container,
  Button,
  Row,
  Col,
  FloatingLabel,
  Form,
} from "react-bootstrap";
import axios from "axios";

import Topbar from "../Topbar";

import React, { useState } from "react";
export default function CenterSubject() {
  const [state, setState] = useState({
    book_name: "",
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",

      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",

  });
  const submit = () => {
    axios
      .post("http://localhost:5000/", {
        book_name: state.book_name,
        Author_Introduction: state.Author_Introduction,
        Meaning_of_Revelation: state.Meaning_of_Revelation,
        History_of_Quran_Revelation: state.History_of_Quran_Revelation,
        History_of_Preservation_of_Quran:
          state.History_of_Preservation_of_Quran,
        Misconceptions_about_Tafsir: state.Misconceptions_about_Tafsir,

        surah: [{
          name: state.name,
          number: state.number,
          versesNumber: state.versesNumber,
          verses: state.verses,
          summary: state.summary,
          ancillary_issues: state.ancillary_issues,
        }]
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  };
  return (
    <>
      <Routes>
        <Route
          path="/"
          element={Md > window.outerWidth ? <CenterMobile /> : <Center />}
        />
        <Route path="admin" element={<Admin />} />
      </Routes>

      <Footer />
    </>
  );
}
