import { FloatingLabel,Form } from "react-bootstrap";

export default function Input({placehold,h,value,set,stat}) {


  const stateHandler = (e) => {
    set(() => {(
      {
        book_name: e.target.value,
          Author_Introduction: e.target.value,
            Meaning_of_Revelation: e.target.value,
              History_of_Quran_Revelation: e.target.value,
                History_of_Preservation_of_Quran: e.target.value,
                  Misconceptions_about_Tafsir: e.target.value,
                    surah: {
          name: e.target.value,
            number: e.target.value,
              versesNumber: e.target.value,
                verses: e.target.value,
                  summary: e.target.value,
                    ancillary_issues: e.target.value,
        }
      }
    )})
  }
    return (
      <>

        <h3>{placehold}</h3>
        <h3>{stat}</h3>
        <FloatingLabel controlId="floatingTextarea2" label={placehold}>
          <Form.Control
            as="textarea"
            placeholder="vsefvsdvfs"
            style={{ height: h }}
           value={value}
            onChange={stateHandler}

          />
        </FloatingLabel>
      </>
    );
}
