import { Container, Button, Row, Col } from "react-bootstrap";
import Input from "./Input.js";

import Topbar from "./Topbar";

import React, { useState } from "react";
export default function CenterSubject() {
  const [state, setState] = useState({
    book_name: "",
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",
    surah: {
      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",
    },
  });
  return (
    <>
      <Topbar />
      <Container className="text-center">
        <Row>
          <Col>
            <Input
              stat={state}
              value={state.book_name}
              set={setState}
              placehold="বইয়ের নাম"
              h="6px"
            />
          </Col>
          <Col>
            <Input
              stat={state}
              value={state.name}
              set={setState}
              placehold="সুরার নাম"
              h="5px"
            />
          </Col>
        </Row>
        <Row>
          <Col>
            <Input
              stat={state}
              value={state.number}
              set={setState}
              placehold="সুরার নাম্বার"
              h="6px"
            />
          </Col>
          <Col>
            <Input
              stat={state}
              value={state.versesNumber}
              set={setState}
              placehold="আয়াত নাম্বার"
              h="5px"
            />
          </Col>
        </Row>

        <Input
          stat={state}
          value={state.verses}
          set={setState}
          placehold="আয়াত বাংলা ,আরকি"
          h="165px"
        />
        <Input
          stat={state}
          value={state.summary}
          set={setState}
          placehold="সারসংক্ষেপ"
          h="295px"
        />
        <Input
          stat={state}
          value={state.ancillary_issues}
          set={setState}
          placehold="আনুসঞ্জিক বিষয়"
          h="295px"
        />
        <div className=" py-25 m-25 bg-warning"> BIG DATA</div>
        <Input
          stat={state}
          value={state.Author_Introduction}
          set={setState}
          placehold="লেখকের পরিচিতি"
          h="295px"
        />
        <Input
          stat={state}
          value={state.Meaning_of_Revelation}
          set={setState}
          placehold="ওহির তাৎর্পয়"
          h="295px"
        />
        <Input
          stat={state}
          value={state.History_of_Quran_Revelation}
          set={setState}
          placehold="কোরআন নাজিলের ইতিহাস"
          h="295px"
        />
        <Input
          stat={state}
          value={state.book_name}
          set={setState}
          placehold="কোরআন সংরক্ষনের ইতিহাস"
          h="295px"
        />
        <Input
          stat={state}
          value={state.book_name}
          set={setState}
          placehold="তাফসির সম্পর্কে ভুল ধারণা"
          h="295px"
        />
        <div className="d-grid gap-2">
          <Button variant="secondary" size="lg">
            Block level button
          </Button>
        </div>
      </Container>
    </>
  );
}
