import {
  Container,
  Button,
  Row,
  Col,
  FloatingLabel,
  Form,
} from "react-bootstrap";
import axios from "axios";

import Topbar from "./Topbar";

import React, { useState } from "react";
export default function CenterSubject() {
  const [state, setState] = useState({
    book_name: "",
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",

      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",

  });
  const submit = () => {
    axios
      .post("http://localhost:5000/", {
       book_name: sta,
    Author_Introduction: "",
    Meaning_of_Revelation: "",
    History_of_Quran_Revelation: "",
    History_of_Preservation_of_Quran: "",
    Misconceptions_about_Tafsir: "",

      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  };
  return (
    <>
      {state.name}
      <Topbar />
      <Container className="text-center m-4">
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="বইয়ের নাম">
              <Form.Control
                as="textarea"
                placeholder="বইয়ের নাম"
                style={{ height: "50px" }}
                value={state.book_name}
                onChange={(e) => {
                  setState({ ...state, book_name: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>

          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="সুরার নাম">
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম"
                style={{ height: "50px" }}
                value={state.name}
                onChange={(e) => {
                  setState({ ...state, name: e.target.value});
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="সুরার নাম্বার">
              <Form.Control
                as="textarea"
                placeholder="সুরার নাম্বার"
                style={{ height: "50px" }}
                value={state.number}
                onChange={(e) => {
                  setState({ ...state,  number: e.target.value});
                }}
              />
            </FloatingLabel>
          </Col>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="আয়াত নাম্বার">
              <Form.Control
                as="textarea"
                placeholder="আয়াত নাম্বার"
                style={{ height: "50px" }}
                value={state.versesNumber}
                onChange={(e) => {
                  setState({
                    ...state,
                    surah: { versesNumber: e.target.value },
                  });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <FloatingLabel controlId="floatingTextarea2" label="আয়াত বাংলা ,আরকি">
          <Form.Control
            as="textarea"
            placeholder="আয়াত বাংলা ,আরকি"
            style={{ height: "165px" }}
            value={state.verses}
            onChange={(e) => {
              setState({
                ...state,
                verses: e.target.value ,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="সারসংক্ষেপ">
          <Form.Control
            as="textarea"
            placeholder="সারসংক্ষেপ"
            style={{ height: "295px" }}
            value={state.summary}
            onChange={(e) => {
              setState({
                ...state,
              summary: e.target.value ,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="আনুসঞ্জিক বিষয়">
          <Form.Control
            as="textarea"
            placeholder="আনুসঞ্জিক বিষয়"
            style={{ height: "295px" }}
            value={state.ancillary_issues}
            onChange={(e) => {
              setState({
                ...state,
               ancillary_issues: e.target.value ,
              });
            }}
          />
        </FloatingLabel>

        <div className=" py-25 m-25 bg-warning"> BIG DATA</div>
        <FloatingLabel controlId="floatingTextarea2" label="আনুসঞ্জিক বিষয়">
          <Form.Control
            as="textarea"
            placeholder="লেখকের পরিচিতি"
            style={{ height: "295px" }}
            value={state.Author_Introduction}
            onChange={(e) => {
              setState({
                ...state,
                Author_Introduction: e.target.value,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="ওহির তাৎর্পয়">
          <Form.Control
            as="textarea"
            placeholder="ওহির তাৎর্পয়"
            style={{ height: "295px" }}
            value={state.Meaning_of_Revelation}
            onChange={(e) => {
              setState({
                ...state,
                Meaning_of_Revelation: e.target.value,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel
          controlId="floatingTextarea2"
          label="কোরআন নাজিলের ইতিহাস"
        >
          <Form.Control
            as="textarea"
            placeholder="কোরআন নাজিলের ইতিহাস"
            style={{ height: "295px" }}
            value={state.History_of_Quran_Revelation}
            onChange={(e) => {
              setState({
                ...state,
                History_of_Quran_Revelation: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <FloatingLabel
          controlId="floatingTextarea2"
          label="কোরআন সংরক্ষনের ইতিহাস"
        >
          <Form.Control
            as="textarea"
            placeholder="কোরআন সংরক্ষনের ইতিহাস"
            style={{ height: "295px" }}
            value={state.History_of_Preservation_of_Qura}
            onChange={(e) => {
              setState({
                ...state,
                History_of_Preservation_of_Quran: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <FloatingLabel
          controlId="floatingTextarea2"
          label="কোরআন সংরক্ষনের ইতিহাস"
        >
          <Form.Control
            as="textarea"
            placeholder="তাফসির সম্পর্কে ভুল ধারণা"
            style={{ height: "295px" }}
            value={state.Misconceptions_about_Tafsir}
            onChange={(e) => {
              setState({
                ...state,
                Misconceptions_about_Tafsir: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <div className="d-grid gap-2">
          <Button variant="secondary" size="lg" type="submit" onClick={submit} >
            Block level button
          </Button>
        </div>
      </Container>
    </>
  );
}
