import {useState,useEffect}from 'react'
import { Form, FloatingLabel, Container, Row, Col, Button } from "react-bootstrap";
import Classess from "../../../css/index.module.css";
import { NavLink } from "react-router-dom";
import  axios from 'axios'
import("dotenv").config();
export default function Quran() {
  const [quran, setQuran] = useState({
    book: "" ,
      name: "" ,
      number: "" ,
      versesNumber: "" ,
      verses: "" ,
      summary: "" ,
      ancillary_issues: "" ,
  });







  const sub = () => {
       axios
         .post(`${process.env.SERVER_URL}/quran`, quran)
         .then((data) => {
           console.log(data.data);
           alert(data.data);
         })
         .catch((err) => console.log({ message: err }));

}

  return (
    <>
      <div className="d-flex flex-column mt-5 mx-auto">
        <NavLink
          className={({ isActive }) =>
            isActive
              ? `${Classess.deleteUpdateHover} ${Classess.deleteUpdateActive} px-4   border border-warning`
              : `${Classess.deleteUpdateHover} ${Classess.deleteUpdateUnActive} px-4  border border-info`
          }
          to="book"
        >
          <h5>Edite And Delete</h5>
        </NavLink>
      </div>

      <Container>
        <Row className="d-grid justify-content-center">
          <Col>
            <strong>Quran</strong>
          </Col>
        </Row>
        <Row>
          <Col>
            <FloatingLabel
              controlId="floatingInput"
              label="বইয়ের নাম"
              className="mb-3"
            >
              <Form.Control
                type="text"
                placeholder="বইয়ের নাম"
                value={quran.book}
                onChange={(e) => {
                  setQuran({ ...quran, book: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
          <Col>
            <FloatingLabel controlId="floatingPassword" label="সুরার নাম">
              <Form.Control
                type="text"
                placeholder="সুরার নাম"
                value={quran.name}
                onChange={(e) => {
                  setQuran({ ...quran, name: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <Row>
          <Col>
            <FloatingLabel
              controlId="floatingInput"
              label="সুরার নাম্বার"
              className="mb-3"
            >
              <Form.Control
                type="text"
                placeholder="সুরার নাম্বার"
                value={quran.number}
                onChange={(e) => {
                  setQuran({ ...quran, number: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
          <Col>
            <FloatingLabel controlId="floatingPassword" label="আয়াত নাম্বার">
              <Form.Control
                type="text"
                placeholder="আয়াত নাম্বার"
                value={quran.versesNumber}
                onChange={(e) => {
                  setQuran({ ...quran, versesNumber: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>

        <Row>
          <Col>
            <FloatingLabel
              controlId="floatingTextarea2"
              label="আয়াত বাংলা ,আরকি"
            >
              <Form.Control
                as="textarea"
                placeholder="আয়াত বাংলা ,আরকি"
                style={{ height: "200px" }}
                className="mb-3"
                value={quran.verses}
                onChange={(e) => {
                  setQuran({ ...quran, verses: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>

        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="সারসংক্ষেপ">
              <Form.Control
                as="textarea"
                placeholder="সারসংক্ষেপ"
                style={{ height: "300px" }}
                className="mb-3"
                value={quran.summary}
                onChange={(e) => {
                  setQuran({ ...quran, summary: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>

        <Row>
          <Col>
            <FloatingLabel controlId="floatingTextarea2" label="আনুসঞ্জিক বিষয়">
              <Form.Control
                as="textarea"
                placeholder="আনুসঞ্জিক বিষয়"
                style={{ height: "400px" }}
                className="mb-3"
                value={quran.ancillary_issues}
                onChange={(e) => {
                  setQuran({ ...quran, ancillary_issues: e.target.value });
                }}
              />
            </FloatingLabel>
          </Col>
        </Row>
        <div className="d-grid gap-2">
          <Button
            type="submit"
            onClick={sub}
            className="mb-3"
            variant="secondary"
            size="sm"
          >
            Quran Input Submit
          </Button>
        </div>
      </Container>
    </>
  );
}
