import {
  Container,
  Button,
  Row,
  Col,
  FloatingLabel,
  Form,
} from "react-bootstrap";
import axios from "axios";



import React, { useState } from "react";
export default function () {
  const [state, setState] = useState({
    book: "",

      name: "",
      number: "",
      versesNumber: "",
      verses: "",
      summary: "",
      ancillary_issues: "",

  });
  const submit = () => {
    axios
      .post("http://localhost:5000/", {
        book_name: state.book_name,
        Author_Introduction: state.Author_Introduction,
        Meaning_of_Revelation: state.Meaning_of_Revelation,
        History_of_Quran_Revelation: state.History_of_Quran_Revelation,
        History_of_Preservation_of_Quran:
          state.History_of_Preservation_of_Quran,
        Misconceptions_about_Tafsir: state.Misconceptions_about_Tafsir,

        surah: [{
          name: state.name,
          number: state.number,
          versesNumber: state.versesNumber,
          verses: state.verses,
          summary: state.summary,
          ancillary_issues: state.ancillary_issues,
        }]
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  };
  return (
    <>
      {state.name}

      <Container className="text-center m-4">
        <Row>
     <Col>

        <FloatingLabel controlId="floatingTextarea2" label="আনুসঞ্জিক বিষয়">
          <Form.Control
            as="textarea"
            placeholder="লেখকের পরিচিতি"
            style={{ height: "295px" }}
            value={state.Author_Introduction}
            onChange={(e) => {
              setState({
                ...state,
                Author_Introduction: e.target.value,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel controlId="floatingTextarea2" label="ওহির তাৎর্পয়">
          <Form.Control
            as="textarea"
            placeholder="ওহির তাৎর্পয়"
            style={{ height: "295px" }}
            value={state.Meaning_of_Revelation}
            onChange={(e) => {
              setState({
                ...state,
                Meaning_of_Revelation: e.target.value,
              });
            }}
          />
        </FloatingLabel>
        <FloatingLabel
          controlId="floatingTextarea2"
          label="কোরআন নাজিলের ইতিহাস"
        >
          <Form.Control
            as="textarea"
            placeholder="কোরআন নাজিলের ইতিহাস"
            style={{ height: "295px" }}
            value={state.History_of_Quran_Revelation}
            onChange={(e) => {
              setState({
                ...state,
                History_of_Quran_Revelation: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <FloatingLabel
          controlId="floatingTextarea2"
          label="কোরআন সংরক্ষনের ইতিহাস"
        >
          <Form.Control
            as="textarea"
            placeholder="কোরআন সংরক্ষনের ইতিহাস"
            style={{ height: "295px" }}
            value={state.History_of_Preservation_of_Qura}
            onChange={(e) => {
              setState({
                ...state,
                History_of_Preservation_of_Quran: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <FloatingLabel
          controlId="floatingTextarea2"
          label="তাফসির সম্পর্কে ভুল ধারণা"
        >
          <Form.Control
            as="textarea"
            placeholder="তাফসির সম্পর্কে ভুল ধারণা"
            style={{ height: "295px" }}
            value={state.Misconceptions_about_Tafsir}
            onChange={(e) => {
              setState({
                ...state,
                Misconceptions_about_Tafsir: e.target.value,
              });
            }}
          />
        </FloatingLabel>

        <div className="d-grid gap-2">
          <Button variant="secondary" size="lg" type="submit" onClick={submit}>
            Block level button
          </Button>
                  </div>
              </Col>
              </Row>
      </Container>
    </>
  );
}
